<?php get_header(); global $post;?><section class="main-container clearfix">	<section class="main wrapper clearfix">		<section class="inner-main-container clearfix">        	<div class="left-container">			<div class="blog-lists">				<?php if(have_posts()):?>                    <?php while(have_posts()): the_post();?>					                                               	<header class="content-title">                                <h1><?php the_title();?></h1>                            </header>                            <article id="post-<?php the_ID(); ?>" <?php post_class(); ?>>                                                                <div class="single-content-area clearfix">                                    <?php if(has_post_thumbnail()):?>										<figure class="single-image-area">                                        																		<?php $image = wp_get_attachment_image_src(get_post_thumbnail_id(get_the_ID()), 'large');?>										<?php //$image = vt_resize(get_post_thumbnail_id(get_the_ID()), '', 271, 198, false);?>										<img src="<?php echo $image[0];?>" alt="<?php the_post_thumbnail_caption(get_the_ID());?>" class="rounded_image">                                    </figure>																		 <?php endif;?>									 <p class="location">										<strong>Location:</strong> 										<span class="date-location-a"><?php echo get_field('location');?></span>									</p>									<?php $dates = get_field('dates');?>									<p class="dates">										<strong>Dates:</strong>										<?php foreach ($dates as $dt):?>											<?php if(strtotime(date('Y-m-d', time())) <= strtotime($dt['start_date'])):?>											<span class="date-location-a"><?php echo '<br/>';echo date('d F, Y', strtotime($dt['start_date']))?> - <?php echo date('d F, Y', strtotime($dt['end_date']));?> <?php echo ($dt['time'] <> '')? "(".$dt['time'].")":'';?></span>										<?php endif;?>										<?php endforeach;?>																				</p>									<p class="location">										<strong>Duration:</strong> 										<span class="date-location-a"><?php echo get_field('course_length');?></span>									</p>									<p class="location">										<strong>Price:</strong> 										<span class="date-location-a">										<?php 											echo '$'.str_replace('$', '',get_field('price'));											$cut_off_date = get_field('early_bird_cut_off_date', get_the_ID(), true);											$cut_off_price = str_replace('$', '',get_field('early_bird_price', get_the_ID(), true));														if(date('Y-m-d',strtotime($cut_off_date)) >= date('Y-m-d', strtotime('now'))){																								echo ' { $'.$cut_off_price.' Early Bird offer to be paid by: '.date('d F Y', strtotime($cut_off_date)).' }';											}										?>										</span>									</p>									<form name="book_me" action="<?php echo site_url();?>/book-now" method="POST">                                        <input type="hidden" name="event_id" value="<?php echo $post->ID;?>">                                        <input type="submit" name="book_me" value="Book Me" class="btn">                                    </form>									<p>&nbsp;</p>                                    <div class="single-content-section">                                        <?php the_content();?>	                                    </div>									                                </div>                            </article>                                       <?php endwhile;?>                <?php endif;?>            </div>            </div>			<div class="right-container">				<section class="sidebar-area">									<div class="widget-area">						<div class="widget">							<h3 class="widget-title"><?php echo get_field('events_course_text', 'option');?></h3>							<?php $term_lists = wp_get_post_terms($post->ID, 'course-categories', array('orderby' => 'id', 'order' => 'ASC', 'fields' => 'all'));?>							<?php if(!empty($term_lists)):?>							<div class="widget-content">																<ul class="widget-list widget-list-budge">																		<?php foreach($term_lists as $term):?>									<?php 	if(get_taxonomy_post_type_posts_count('events', $term->term_id) == 0){												continue;											}									?>									<li><a href="<?php echo get_term_link($term->term_id, 'course-categories');?>"><?php echo $term->name;?> <span class="budge-list"><?php echo get_taxonomy_post_type_posts_count('events', $term->term_id);?></span></a></li>									<?php endforeach;?>																	</ul>							</div>							<?php else: ?>							<div class="widget-content">																<p>No related courses.</p>							</div>							<?php endif; ?>						</div>                               					</div>									</section>			</div>		</section>	</section> <!-- #main --></section> <!-- #main-container --><?php get_footer();?>